mapper.select:

select

  distinct


from operation




  order by ${orderByClause}


  limit #{limitStart}, #{limitSize}

    SELECT o.id AS operation_id, o.operator AS operation_operator , o.time AS operation_time, o.type AS operation_type, o.result AS operation_result, o.lineseat AS operation_lineseat, o.material_no AS operation_material_no,
    o.old_material_no AS operation_old_material_no, o.scanlineseat AS operation_scanlineseat, o.remark AS operation_remark, o.program_id AS operation_program_id, o.work_order AS operation_work_order, o.board_type AS operation_board_type,
    line.line AS line_name, line.id AS line_id, p.specitification AS program_item_specitification
	FROM operation o JOIN line JOIN program_item p
	ON 
		line.id = o.line 
		AND p.lineseat = o.lineseat 
		AND p.program_id = o.program_id 
		AND p.material_no = o.material_no
	WHERE o.result = 'PASS'

      		and o.program_id in 
      		(SELECT id FROM program 
      		WHERE 1 =1 

      		and program.client LIKE #{client}


      		and program.program_no like #{programNo}


      		and program.work_order like #{orderNo}

      		)


      		and o.line = #{line}


      		and o.work_order like #{workOrderNo}


      		and o.time  > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


      		and o.time  < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

		ORDER BY o.time DESC

      		limit #{firstIndex}, #{pageSize}

SELECT o.id AS operation_id, o.operator AS operation_operator , o.time AS operation_time, o.type AS operation_type, o.result AS operation_result, o.lineseat AS operation_lineseat, o.material_no AS operation_material_no, o.old_material_no AS operation_old_material_no, o.scanlineseat AS operation_scanlineseat, o.remark AS operation_remark, o.program_id AS operation_program_id, o.work_order AS operation_work_order, o.board_type AS operation_board_type, line.line AS line_name, line.id AS line_id, p.specitification AS program_item_specitification FROM operation o JOIN line JOIN program_item p ON line.id = o.line AND p.lineseat = o.lineseat AND p.program_id = o.program_id AND p.material_no = o.material_no WHERE 1=1
and o.type = #{type}
and o.line = #{line}
and o.work_order = #{workOrderNo}
and o.operator = #{operator}
and o.time > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')
and o.time < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')
ORDER BY o.time DESC
limit #{firstIndex}, #{pageSize}

SELECT o.operator AS operation_operator, o.work_order AS operation_work_order, line.line AS line_name, line.id AS line_id, SUM(IF(o.result = 'PASS', 1, 0)) AS operation_pass_count, SUM(IF(o.result = 'FAIL', 1, 0)) AS operation_fail_count FROM operation o JOIN line JOIN program_item p ON line.id = o.line AND p.lineseat = o.lineseat AND p.program_id = o.program_id AND p.material_no = o.material_no WHERE 1=1
and o.type = #{type}
and o.line = #{line}
and o.work_order like #{workOrderNo}
and o.time > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')
and o.time < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')
GROUP BY o.operator, o.work_order, o.line ORDER BY o.time DESC
limit #{firstIndex}, #{pageSize}
