mapper.select:

select

  distinct


from operation




  order by ${orderByClause}


  limit #{limitStart}, #{limitSize}

    SELECT operation.* from operation INNER JOIN program on program.id = operation.program_id
	where operation.result = 'PASS'

      		and program.client like #{client}


      		and program.program_no like #{programNo}


      		and program.work_order like #{orderNo}


      		and operation.line = #{line}


      		and operation.work_order like #{workOrderNo}


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

		ORDER BY operation.time DESC

      		limit #{firstIndex}, #{pageSize}

    SELECT count(*) from operation INNER JOIN program on program.id = operation.program_id 
	where operation.result = 'PASS'

      		and program.client like #{client}


      		and program.program_no like #{programNo}


      		and program.work_order like #{orderNo}


      		and operation.line = #{line}


      		and operation.work_order like #{workOrderNo}


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

    SELECT operation.* from operation INNER JOIN program on program.id = operation.program_id
	where 1=1

      		and operation.type = #{type}


      		and program.client like #{client}


      		and operation.line = #{line}


      		and operation.work_order like #{workOrderNo}


      		and operation.operator like #{operator}


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')

		ORDER BY operation.time DESC

      		limit #{firstIndex}, #{pageSize}

    SELECT count(*) from operation INNER JOIN program on program.id = operation.program_id
	where 1=1

      		and operation.type = #{type}


      		and program.client like #{client}


      		and operation.line = #{line}


      		and operation.work_order like #{workOrderNo}


      		and operation.operator like #{operator}


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') > DATE_FORMAT(#{startTime}, '%Y-%m-%d %H:%i:%S')


      		 and DATE_FORMAT(operation.time, '%Y-%m-%d %H:%i:%S') < DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%S')
